    state={
        text: '',
        date: new Date()

    }
    render(){
        return(
            <div className='App'>

                <h1>Agenda du Rappel</h1>
                <input
                 type='text' placeholder="quel Ã©venement ?" className="form-control"
                 onChange={(e)=>this.setState({text:e.target.value})}
                 /><br/>
                <input 
                type="date" className="form-control"
                onChange={(e)=>this.setState({date:e.target.value})}
                /><br/>

                <button className="btn btn-primary btn-block" onClick={()=>add_Reminder()}>Ajouter</button>
                <button className="btn btn-danger btn-block">Supprimer</button>
            </div>
        )
    }
}
